mapper.insert:
INSERT INTO file_table (
    file_code, target_type, target_code, file_path, file_name, file_size, file_order
) VALUES (
    #{file_code}, #{target_type}, #{target_code}, #{file_path}, #{file_name}, #{file_size}, #{file_order}
)

mapper.select:
SELECT * FROM file_table 
WHERE target_type = #{target_type} AND target_code = #{target_code}
ORDER BY file_order ASC
