mapper.select:
SELECT * FROM (
    SELECT inner.*, ROWNUM AS rnum
    FROM (
        SELECT * FROM report 
        WHERE rep_status = 'PENDING'
        ORDER BY created_at DESC
    ) inner
    WHERE ROWNUM <= #{endRow}
)
WHERE rnum >= #{startRow}

SELECT * FROM (
    SELECT inner.*, ROWNUM AS rnum
    FROM (
        SELECT * FROM report 
        WHERE rep_status = 'DONE'
        ORDER BY created_at DESC
    ) inner
    WHERE ROWNUM <= #{endRow}
)
WHERE rnum >= #{startRow}

SELECT * FROM (
    SELECT inner.*, ROWNUM AS rnum
    FROM (
        SELECT * FROM report 
        WHERE rep_status = 'IGNORE'
        ORDER BY created_at DESC
    ) inner
    WHERE ROWNUM <= #{endRow}
)
WHERE rnum >= #{startRow}

 	SELECT rep_code, rep_category, m_code, rep_title, rep_content, target_code, created_at, rep_status
 	FROM report
		WHERE rep_code = #{rep_code}

mapper.update:
UPDATE report
SET rep_status = #{rep_status}
WHERE rep_code = #{rep_code}
